import { ACTION_TYPES } from "../actions/postMessage";

const intialState = {
  list: []
}

export const postMessage = (state = intialState, action) => {
  switch (action.type) {
    case ACTION_TYPES.FETCH_ALL:
      return {
        ...state,
        list: [...action.payload]
      }

    case ACTION_TYPES.CREATE:
      return {
        ...state,
        list: [...state.list, action.payload]
      }


    default:
      return state;
  }
}
