import React, { Component, useState, useEffect } from 'react';
import Main from "./Main";
import './App.css';
import './index.css';
import { Provider } from "react-redux";
import PostMessages from "./components/PostMessages";
import { store } from "./actions/store";
import { Container, AppBar, Typography, withStyles, Button, Grid } from "@material-ui/core";
import ButterToast, { POS_RIGHT, POS_TOP } from "butter-toast";



const styles = theme => ({
  someClass: {
    margin: theme.spacing(2),
    padding: theme.spacing(2),
    BackgroundColor: "AliceBlue"

  },
  otherClass: {
    background: 'AliceBlue'
  },
  upperBtns1: {
    width: "40%",
    marginLeft: theme.spacing(-80)
  },
  upperBtns2: {
    width: "40%",
    marginLeft: theme.spacing(-60)
  },
  actionDiv: {
    textAlign: "center",
    marginLeft: theme.spacing(93)
  }
});

//class App extends Component {
const MyComponent = (props) => {



  const onNewBtn = () => {
    console.log("Button is pressed")
  }

  useState = {
    renderView: 0
  };

  // const clickBtn = e => {
  //   // this.setState({
  //   //   renderView: +e.target.value
  //   // });
  //   const renderView = 0
  //   renderView = +e.target.value
  //   return <PostMessages />
  // };

  // const clickBtn = e => {
  //   //console.log("Hi")
  //   let renderView = 0;
  //   renderView = + e.target.value
  //   console.log(renderView)


  // };

  clickBtn = e => {
    setState({
      renderView: +e.target.value
    });
  };

  //render()
  switch (props.renderView) {
    //switch (state.renderView) {
    case 1:
      console.log("Hi")
      return <PostMessages />;
    case 2:
    //return <View2 />;
    default:
      //return <Main clickBtn={this.clickBtn} />;


      return (
        <div>
          <Main clickBtn={clickBtn} />
          <Provider store={store} >
            <Container maxWidth="lg">
              <AppBar position="static" backgroundColor="red" className={props.classes.someClass}>
                <Typography
                  //className={props.classes.otherClass}
                  variant="h3"
                  align="left">
                  TEXT  MANAGER
          </Typography>
              </AppBar>
              <Grid item xs={12}>

                <div>
                  <div className={props.classes.actionDiv}>
                    <Button variant="contained" color="primary" size="large"
                      className={props.classes.upperBtns1}

                    >
                      View Text
                                                    </Button>
                    <Button variant="contained" color="primary" size="large"
                      className={props.classes.upperBtns2}
                      onClick={() => onNewBtn()}
                    >
                      New Text
                                                    </Button>{" "}
                  </div>
                </div>

              </Grid>
              {/* <PostMessages /> */}
              <ButterToast position={{ vertical: POS_TOP, horizontal: POS_RIGHT }} />
            </Container>
          </Provider>
        </div>
      );
  }

}


export default withStyles(styles)(MyComponent);
//export default MyComponent;
