import React, { useEffect, useState } from "react";
import { TextField, withStyles } from "@material-ui/core";

const initialFieldValue = {
 title: '',
 message: ''
}

const styles = theme => ({
 root: {
  '& .MuiTextField-root': {
   margin: theme.spacing(1)
  },
 },
})

//This is a Component PostMessageForm
const PostMessageForm = (props) => {
 const [values, setValues] = useState(initialFieldValue)
 return (
  <form autoComplete="off" noValidate>
   <TextField
    name="title"
    variant="outlined"
    label="Title"
    fullWidth
    value={values.title}
   ></TextField>
   <TextField
    name="message"
    variant="outlined"
    label="Message"
    fullWidth
    value={values.message}
   ></TextField>
  </form>
 );
}

export default withStyles(styles)(PostMessageForm);
