import React, { useEffect, useState, Fragment } from "react";
import { connect } from "react-redux";
import * as actions from "../actions/postMessage";
import { Grid, Paper, Button, withStyles, List, ListItem, Typography, ListItemText, Divider } from "@material-ui/core";
import PostMessageForm from "./PostMessageForm";


const styles = theme => ({
 paper: {
  margin: theme.spacing(3),
  padding: theme.spacing(2)
 },
 actionDiv: {
  textAlign: "center"
 },
 smMargin: {
  margin: theme.spacing(1)
 },
})

//This is a Component PostMessages
const PostMessages = ({ classes, ...props }) => {

 useEffect(() => {
  props.fetchAllPostMessages()
 })

 return (
  <Grid container>
   <Grid item xs={5}>
    <Paper className={classes.paper}>
     <PostMessageForm />
    </Paper>
   </Grid>
   <Grid item xs={7}>
    <Paper className={classes.paper}>
     <List>
      {
       props.postMessageList.map((record, index) => {
        return (
         <Fragment key={index}>
          <ListItem>
           <ListItemText>
            <Typography variant="h5">
             {record.title}
            </Typography>
            <div>
             {record.message}
            </div>
            <div className={classes.actionDiv}>
             <Button variant="contained" color="primary" size="small"
              className={classes.smMargin}
              onClick={() => setCurrentId(record._id)}>
              Edit
                                                    </Button>
             <Button variant="contained" color="secondary" size="small"
              className={classes.smMargin}
              onClick={() => onDelete(record._id)}>
              Delete
                                                    </Button>
            </div>
           </ListItemText>
          </ListItem>
          <Divider component="li" />
         </Fragment>
        )
       })
      }
     </List>
    </Paper>
   </Grid>
  </Grid>
 );
}

const mapStateToProps = state => ({
 postMessageList: state.postMessage.list
})

const mapActionToProps = {
 fetchAllPostMessages: actions.fetchAll,
 deletePostMessage: actions.Delete
}

export default connect(mapStateToProps, mapActionToProps)(withStyles(styles)(PostMessages));
