import React, { useEffect, useState } from "react";
import { TextField, withStyles, Button } from "@material-ui/core";
import useForm from "./useForm";

const initialFieldValue = {
 title: '',
 message: ''
}

const styles = theme => ({
 root: {
  '& .MuiTextField-root': {
   margin: theme.spacing(1)
  },
 },
 form: {
  display: 'flex',
  flexWrap: 'wrap',
  justifyContent: 'center'
 },
 postBtn: {
  width: "50%"
 }
})



//This is a Component PostMessageForm
const PostMessageForm = ({ classes, ...props }) => {

 const validate = () => {
  let temp = { ...errors }
  temp.title = values.title ? "" : "This Field is required"
  temp.message = values.message ? "" : "This Field is required"
  return Object.values(temp).every(x => x == "")
 }

 var { values,
  setValues,
  errors,
  setErrors,
  handleInputChange } = useForm(initialFieldValue)

 const handleSubmit = e => {
  e.preventDefault();
  if (validate())
   window.alert('Validation Succeeded')
 }

 return (
  <form autoComplete="off" noValidate className={`${classes.root} ${classes.form}`} onSubmit={handleSubmit} >
   <TextField
    name="title"
    variant="outlined"
    label="Title"
    fullWidth
    value={values.title}
    onChange={handleInputChange}
   ></TextField>
   <TextField
    name="message"
    variant="outlined"
    label="Message"
    fullWidth
    value={values.message}
    multiline rows={4}
    onChange={handleInputChange}
   ></TextField>
   <Button
    variant="contained"
    color="primary"
    size="large"
    type="submit"
    className={classes.postBtn}
   >Submit</Button>
  </form >
 );
}

export default withStyles(styles)(PostMessageForm);
