import React, { useEffect, Fragment } from "react";
import ReactDOM from "react-dom";
import { connect } from "react-redux";
import * as actions from "../actions/postMessage";
import { Grid, Paper, withStyles, List, ListItem, Typography, ListItemText, Divider } from "@material-ui/core";
import PostMessageForm from "./PostMessageForm";


const styles = theme => ({
 paper: {
  margin: theme.spacing(3),
  padding: theme.spacing(2)
 }
})

//This is a Component PostMessages
const PostMessages = ({ classes, ...props }) => {

 useEffect(() => {
  props.fetchAllPostMessages()
 })

 return (
  <Grid container>
   <Grid item xs={5}>
    <Paper className={classes.paper}>
     <PostMessageForm />
    </Paper>
   </Grid>
   <Grid item xs={7}>
    <Paper className={classes.paper}>
     <div>
      <List>
       {
        props.postMessageList.map((record, index) => {
         return (
          <Fragment>
           <ListItem>
            <ListItemText>
             <Typography variant="h5">
              {record.title}
             </Typography>
             <div>
              {record.message}
             </div>
            </ListItemText>
           </ListItem>
           <Divider component="li" />
          </Fragment>
         )
        })
       }
      </List>
     </div>
    </Paper>
   </Grid>
  </Grid>
 );
}

const mapStateToProps = state => ({
 postMessageList: state.postMessage.list
})

const mapActionToProps = {
 fetchAllPostMessages: actions.fetchAll
}

export default connect(mapStateToProps, mapActionToProps)(withStyles(styles)(PostMessages));
