import React, { Component } from 'react';
import Main from "./Main";
import './App.css';
import './index.css';
import { Provider } from "react-redux";
import PostMessages from "./components/PostMessages";
import { store } from "./actions/store";
import { Container, AppBar, Typography, withStyles } from "@material-ui/core";
import ButterToast, { POS_RIGHT, POS_TOP } from "butter-toast";


const styles = theme => ({
  someClass: {
    margin: theme.spacing(2),
    padding: theme.spacing(2),
    BackgroundColor: "AliceBlue"

  },
  otherClass: {
    background: 'AliceBlue'
  },
  upperBtns1: {
    width: "40%",
    marginLeft: theme.spacing(-80)
  },
  upperBtns2: {
    width: "40%",
    marginLeft: theme.spacing(-60)
  },
  actionDiv: {
    textAlign: "center",
    marginLeft: theme.spacing(93)
  }
});

class App extends Component {
  //const MyComponent = (props) => {

  state = {
    renderView: 0
  };

  clickBtn = e => {
    this.setState({
      renderView: +e.target.value
    });
  };

  render() {

    switch (this.state.renderView) {
      case 1:
      //return <View1 />;
      case 2:
      //return <View2 />;
      default:
        //return <Main clickBtn={this.clickBtn} />;


        return (
          <Provider store={store} >
            <Container maxWidth="lg">
              <AppBar position="static" backgroundColor="red" className={styles.someClass}>
                <Typography
                  //className={props.classes.otherClass}
                  variant="h3"
                  align="left">
                  TEXT  MANAGER
          </Typography>
              </AppBar>
              <Grid item xs={12}>

                <div>
                  <div className={styles.actionDiv}>
                    <Button variant="contained" color="primary" size="large"
                      className={styles.upperBtns1}

                    >
                      View Text
                                                    </Button>
                    <Button variant="contained" color="primary" size="large"
                      className={styles.upperBtns2}
                    //onClick={myNewButton}
                    >
                      New Text
                                                    </Button>
                  </div>
                </div>

              </Grid>
              <PostMessages />
              <ButterToast position={{ vertical: POS_TOP, horizontal: POS_RIGHT }} />
            </Container>
          </Provider>
        );
    }
  }
}


export default withStyles(styles)(App);
//export default MyComponent;
