import React, { useEffect, useState } from "react";
import { TextField, withStyles, Button } from "@material-ui/core";
import useForm from "./useForm";
import { connect } from "react-redux";
import * as actions from "../actions/postMessage";

const initialFieldValue = {
 title: '',
 message: ''
}

const styles = theme => ({
 root: {
  '& .MuiTextField-root': {
   margin: theme.spacing(1)
  },
 },
 form: {
  display: 'flex',
  flexWrap: 'wrap',
  justifyContent: 'center'
 },
 postBtn: {
  width: "50%"
 }
})



//This is a Component PostMessageForm
const PostMessageForm = ({ classes, ...props }) => {

 const validate = () => {
  let temp = { ...errors }
  temp.title = values.title ? "" : "This Field is required"
  temp.message = values.message ? "" : "This Field is required"
  setErrors({
   ...temp
  })
  return Object.values(temp).every(x => x == "")
 }

 var { values,
  setValues,
  errors,
  setErrors,
  handleInputChange } = useForm(initialFieldValue)

 const handleSubmit = e => {
  e.preventDefault();
  const onSuccess = () => {
   window.alert('Submitted Successfully')
  }
  if (validate()) {
   props.createPostMessages(values, onSuccess)
  }
 }

 return (
  <form autoComplete="off" noValidate className={`${classes.root} ${classes.form}`} onSubmit={handleSubmit} >
   <TextField
    name="title"
    variant="outlined"
    label="Title"
    fullWidth
    value={values.title}
    onChange={handleInputChange}
    {...(errors.title && { error: true, helperText: errors.title })}
   ></TextField>
   <TextField
    name="message"
    variant="outlined"
    label="Message"
    fullWidth
    value={values.message}
    multiline rows={4}
    onChange={handleInputChange}
    {...(errors.message && { error: true, helperText: errors.message })}
   ></TextField>
   <Button
    variant="contained"
    color="primary"
    size="large"
    type="submit"
    className={classes.postBtn}
   >Submit</Button>
  </form >
 );
}

const mapStateToProps = state => ({
 postMessageList: state.postMessage.list
})

const mapActionToProps = {
 createPostMessages: actions.create,
 updatePostMessages: actions.update
}

export default connect(mapStateToProps, mapActionToProps)(withStyles(styles)(PostMessageForm));
